customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表 25-材质列表
var str = "";
switch (attrs.actorCheckType) {
    case 0:
        str += `玩家队伍角色`;
        str += attrs.actorUseVar ? `「${attrs.actorIDVarID}-${getTypeDataListName(3, attrs.actorIDVarID)}」` : `「${attrs.actorID}-${getModuleDataName(6, attrs.actorID)}」`;
        break;
    case 1:
        str += "玩家队伍位置"
        str += attrs.actorInPartyIndexVarIDUseVar ? `「${attrs.actorInPartyIndexVarID}-${getTypeDataListName(3, attrs.actorInPartyIndexVarID)}」` : `「${attrs.actorInPartyIndex}」`;
        str += "的角色"
        break;
    case 2:
        str += "敌方队伍位置"
        str += attrs.enemyInPartyIndexVarIDUseVar ? `「${attrs.enemyInPartyIndexVarID}-${getTypeDataListName(3, attrs.enemyInPartyIndexVarID)}」` : `「${attrs.enemyInPartyIndex}」`;
        str += "的角色"
        break;
    default:
        str += attrs.actorCheckTypeItems[attrs.actorCheckType];
}

str += " => " + attrs.checkTypeItems[attrs.checkType];

switch (attrs.checkType) {
    case 1:
        str += attrs.skillUseVar ? `「${attrs.skillIDVarID}-${getTypeDataListName(3, attrs.skillIDVarID)}」` : `「${attrs.skillID}-${getModuleDataName(8, attrs.skillID)}」`;
        break;
    case 2:
        str += attrs.equipUseVar ? `「${attrs.equipIDVarID}-${getTypeDataListName(3, attrs.equipIDVarID)}」` : `「${attrs.equipID}-${getModuleDataName(9, attrs.equipID)}」`;
        break;
    case 3:
        str += attrs.statusUseVar ? `「${attrs.statusIDVarID}-${getTypeDataListName(3, attrs.statusIDVarID)}」` : `「${attrs.statusID}-${getModuleDataName(10, attrs.statusID)}」`;
        break;
    case 4:
        str += attrs.classUseVar ? `「${attrs.classIDVarID}-${getTypeDataListName(3, attrs.classIDVarID)}」` : `「${attrs.classID}-${getModuleDataName(7, attrs.classID)}」`;
        break;
}


str;

